import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { auth } from '../Firebase/FirebaseConfig';
import { GoogleAuthProvider, signInWithPopup } from 'firebase/auth'; // Importe as funções necessárias
import Questionario from './Questionario';

function GoogleAuth() {
  const [usuario, setUsuario] = useState(null);
  const [questionarioCompleto, setQuestionarioCompleto] = useState(false);
  const [experiencia, setExperiencia] = useState(''); // Estado para armazenar a experiência

  // Cria uma instância do provedor de autenticação do Google
  const provider = new GoogleAuthProvider();

  // Observa mudanças no estado de autenticação
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        setUsuario(user);
      } else {
        setUsuario(null);
      }
    });
    return () => unsubscribe();
  }, []);

  // Função de Login com Google usando o provider
  const Login = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      const currentUser = result.user;
      if (currentUser) {
        const idToken = await currentUser.getIdToken(true);
        const response = await axios.post('http://localhost:8080/login', { idToken });

        const userInfo = response.data;
        console.log('Informações do usuário:', userInfo);

        // Busca os dados completos do usuário pelo UID
        const userDetails = await axios.get(`http://localhost:8080/user?uid=${userInfo.uid}`);
        setUsuario(userDetails.data);
        setQuestionarioCompleto(userDetails.data.questionarioCompleto);
      }
    } catch (error) {
      console.error('Erro ao fazer login:', error);
    }
  };

  // Função de Logout
  const logout = async () => {
    try {
      await axios.post('http://localhost:8080/logout');
      setUsuario(null);
      setQuestionarioCompleto(false);
      await auth.signOut();
    } catch (error) {
      console.error('Erro ao fazer logout:', error);
    }
  };

  // Função para completar o questionário
  const preencherQuestionario = async () => {
    if (usuario) {
      try {
        await axios.post('http://localhost:8080/enviar-questionario', { uid: usuario.uid });
        setQuestionarioCompleto(true);
      } catch (error) {
        console.error('Erro ao marcar questionário como completo:', error);
      }
    }
  };

  // Função para enviar a experiência ao Firebase
  const enviarExperiencia = async () => {
    if (usuario) {
      try {
        await axios.post('http://localhost:8080/adicionar-experiencia', {
          uid: usuario.uid,
          experiencia,
        });
        console.log('Experiência enviada com sucesso');
        setExperiencia(''); // Limpa o campo após o envio
      } catch (error) {
        console.error('Erro ao enviar a experiência:', error);
      }
    }
  };

  return (
    <div>
      <h1>Informações do Usuário</h1>
      {usuario ? (
        <div>
          <h2>Bem-vindo, {usuario.displayName || 'Usuário'}</h2>
          {usuario.photoURL && <img src={usuario.photoURL} alt="Foto do usuário" />}
          <p>Email: {usuario.email}</p>
          <p>UID: {usuario.uid}</p>
          <p>Questionário Completo: {questionarioCompleto ? 'Sim' : 'Não'}</p>
          <button onClick={logout}>Logout</button>

          {!questionarioCompleto && (
            <div>
              <button onClick={preencherQuestionario}>Completar Questionário</button>
            </div>
          )}

          <div>
            <h3>Adicionar Experiência</h3>
            <textarea
              value={experiencia}
              onChange={(e) => setExperiencia(e.target.value)}
              placeholder="Descreva sua experiência"
            ></textarea>
            <button onClick={enviarExperiencia}>Enviar Experiência</button>
          </div>
        </div>
      ) : (
        <button onClick={Login}>Login com Google</button>
      )}
    </div>
  );
}

export default GoogleAuth;
