import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EditarUsuario'; // Certifique-se de criar este arquivo de estilo para personalizar o visual do modal

function EditarUsuario({ uid }) {
  const [usuario, setUsuario] = useState({
    displayName: '',
    email: '',
    photoURL: '',
    phoneNumber: '',
    bio: '', // Adicionando um campo de exemplo para biografia
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Busca as informações do usuário pelo UID
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/contas/user?uid=${uid}`);
        setUsuario(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Erro ao buscar informações do usuário:', error);
      }
    };

    fetchUserData();
  }, [uid]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUsuario((prevUser) => ({
      ...prevUser,
      [name]: value,
    }));
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:8080/contas/user/${uid}`, usuario);
      alert('Informações atualizadas com sucesso!');
    } catch (error) {
      console.error('Erro ao atualizar as informações:', error);
      alert('Erro ao salvar as alterações.');
    }
  };

  if (loading) {
    return <div>Carregando...</div>;
  }

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Editar Usuário</h2>
          <button className="close-button" onClick={() => setUsuario(null)}>&times;</button>
        </div>
        <div className="modal-body">
          <label>
            Nome:
            <input
              type="text"
              name="displayName"
              value={usuario.displayName}
              onChange={handleChange}
            />
          </label> <br />
          <label>
            Email:
            <input
              type="email"
              name="email"
              value={usuario.email}
              disabled
              onChange={handleChange}
            />
          </label>
          <label>
            URL da Foto:
            <input
              type="text"
              name="photoURL"
              value={usuario.photoURL}
              onChange={handleChange}
            />
          </label>
          <label>
            Telefone:
            <input
              type="text"
              name="phoneNumber"
              value={usuario.phoneNumber}
              onChange={handleChange}
            />
          </label>
          <label>
            Biografia:
            <textarea
              name="bio"
              value={usuario.bio}
              onChange={handleChange}
            />
          </label>
        </div>
        <div className="modal-footer">
          <button className="save-button" onClick={handleSave}>Salvar</button>
        </div>
      </div>
    </div>
  );
}

export default EditarUsuario;
