import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ExibirDesafiosfalso.css'; // Importa o arquivo de estilo

function ExibirDeasfios() {
  const [desafios, setDesafios] = useState([]);
  const [detalhesDesafio, setDetalhesDesafio] = useState(null);
  const [novaSolucao, setNovaSolucao] = useState('');
  const [showDetalhesModal, setShowDetalhesModal] = useState(false); // Estado para exibir o modal de detalhes
  const [showEditModal, setShowEditModal] = useState(false); // Estado para exibir o modal de edição

  const [editDesafio, setEditDesafio] = useState({
    id: '',
    desafio: '',
    recompensa: '',
    dataLimite: '',
    descricao: '',
  });

  useEffect(() => {
    const fetchDesafios = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/desafios`);
        setDesafios(response.data);
      } catch (error) {
        console.error('Erro ao buscar desafios:', error);
      }
    };

    fetchDesafios();
  }, []);

  const handleExibirDetalhes = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8080/desafios/${id}`);
      setDetalhesDesafio(response.data);
      setShowDetalhesModal(true); // Exibe o modal de detalhes
    } catch (error) {
      console.error('Erro ao buscar detalhes do desafio:', error);
    }
  };

  const handleCloseDetalhesModal = () => {
    setShowDetalhesModal(false);
    setDetalhesDesafio(null);
  };

  const handleAdicionarSolucao = async (id) => {
    try {
      await axios.post(`http://localhost:8080/desafios/${id}/solucoes`, {
        solucao: novaSolucao,
      });
      alert('Solução adicionada com sucesso');
      setNovaSolucao('');
      handleExibirDetalhes(id); // Atualiza os detalhes após adicionar
    } catch (error) {
      console.error('Erro ao adicionar solução:', error);
      alert('Erro ao adicionar solução');
    }
  };

  const handleOpenEditModal = (desafio) => {
    setEditDesafio(desafio);
    setShowEditModal(true);
  };

  const handleCloseEditModal = () => {
    setShowEditModal(false);
  };

  const handleEditarDesafio = async () => {
    try {
      await axios.put(`http://localhost:8080/desafios/${editDesafio.id}`, editDesafio);
      setDesafios(
        desafios.map((desafio) =>
          desafio.id === editDesafio.id ? { ...desafio, ...editDesafio } : desafio
        )
      );
      alert('Desafio atualizado com sucesso');
      setShowEditModal(false);
      setEditDesafio({ id: '', desafio: '', recompensa: '', dataLimite: '', descricao: '' });
    } catch (error) {
      console.error('Erro ao editar desafio:', error);
      alert('Erro ao editar desafio');
    }
  };

  return (
    <div className="container">
      <strong>Lista de Desafios</strong>
      {desafios.map((desafio) => (
        <div key={desafio.id} className="card">
          <h2>Nome: {desafio.desafio}</h2>
          <p>Recompensa: {desafio.recompensa}</p>
          <p>Data Limite: {desafio.dataLimite}</p>
          <p>Descrição: {desafio.descricao}</p>
          <button onClick={() => handleExibirDetalhes(desafio.id)} className="button-details">Exibir detalhes</button> <br />
          <button onClick={() => handleOpenEditModal(desafio)} className="button-details">Editar</button>
        </div>
      ))}

      {showDetalhesModal && detalhesDesafio && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={handleCloseDetalhesModal}>&times;</span>
            <h2>Detalhes do Desafio</h2>
            <p>Descrição: {detalhesDesafio.descricao}</p>
            <p>Recompensa: {detalhesDesafio.recompensa}</p>
            <p>Data Limite: {detalhesDesafio.dataLimite ? new Date(detalhesDesafio.dataLimite).toLocaleDateString('pt-BR') : 'Sem data limite'}</p>
            <p>Autor: {detalhesDesafio.autorId}</p>
            <ul>
              <strong>Soluções:</strong>
              {detalhesDesafio.solucoes?.map((solucao, index) => (
                <li key={index}>{solucao}</li>
              ))}
            </ul>
            <input
              type="text"
              placeholder="Adicionar solução"
              value={novaSolucao}
              onChange={(e) => setNovaSolucao(e.target.value)}
            />
            <strong></strong>
            <button onClick={() => handleAdicionarSolucao(detalhesDesafio.id)}>Salvar</button>
          </div>
        </div>
      )}

      {showEditModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={handleCloseEditModal}>&times;</span>
            <h2>Editar Desafio</h2>
            <input
              type="text"
              name="desafio"
              placeholder="Nome do Desafio"
              value={editDesafio.desafio}
              onChange={(e) => setEditDesafio({ ...editDesafio, desafio: e.target.value })}
            />
            <input
              type="text"
              name="recompensa"
              placeholder="Recompensa"
              value={editDesafio.recompensa}
              onChange={(e) => setEditDesafio({ ...editDesafio, recompensa: e.target.value })}
            />
            <input
              type="text"
              name="dataLimite"
              placeholder="Data Limite"
              value={editDesafio.dataLimite}
              onChange={(e) => setEditDesafio({ ...editDesafio, dataLimite: e.target.value })}
            />
            <input
              type="text"
              name="descricao"
              placeholder="Descrição"
              value={editDesafio.descricao}
              onChange={(e) => setEditDesafio({ ...editDesafio, descricao: e.target.value })}
            />
            <button onClick={handleEditarDesafio}>Salvar alterações</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ExibirDeasfios;
