import React, { useState, useEffect } from 'react';
import { db } from '../Firebase/FirebaseConfig';
import { collection, onSnapshot, getDoc, doc } from 'firebase/firestore';
import axios from 'axios';

// HOME SERÁ A PAGINA QUE MOSTRARA PARA O USUARIO TODOS OS DESAFIOS QUE FORAM POSTADOS
// ++ MONTAR A LOGICA DE MOSTRAR OS DESAFIOS MAIS RECENTES 
// ++ MONTAR A LOGICA DE VER QUAIS DESAFIOS AINDA ESTÃO DISPONIVEIS PARA SEREM SOLUCIONADOS

function Home() {
  const [desafios, setDesafios] = useState([]);

  useEffect(() => {
    const fetchDesafios = async () => {
      try {
        const response = await axios.get('http://localhost:8080/desafios'); // Ajuste a URL conforme necessário
        setDesafios(response.data);
      } catch (error) {
        console.error('Erro ao buscar desafios:', error);
      }
    };

    fetchDesafios();
  }, []);

  return (
    <div>
      <strong>ESTA PÁGINA DEVE MOSTRAR TODOS OS DESAFIOS COMO UMA PÁGINA INICIAL</strong>
      {desafios.map((desafio) => (
        <div key={desafio.id}>
          <h1>Nome Desafio: {desafio.desafio}</h1>
          <p>Descrição Desafio: {desafio.descricao}</p>
          <p>Data Limite: {desafio.dataLimite ? new Date(desafio.dataLimite).toLocaleDateString("pt-BR") : "Sem data limite"}</p>
          <p>Recompensa: {desafio.recompensa}</p>
          <p>Critérios: {desafio.criterios}</p>
          <p>Autor: {desafio.autorId}</p>
          <ul>
            <strong>Soluções:</strong>
            {Array.isArray(desafio.solucoes) ? (
              desafio.solucoes.map((solucao) => (
                <li key={solucao.id}>
                  <strong>{solucao.solucao}</strong> <br />
                  <strong>{solucao.usuario}</strong>
                </li>
              ))
            ) : (
              <li>Sem soluções disponíveis</li>
            )}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default Home;
