import React, { useEffect } from 'react';
import { messaging } from './firebase-config';
import './App.css';

function App() {
  useEffect(() => {
    // Solicita permissão para exibir notificações
    messaging.requestPermission()
      .then(() => {
        console.log("Permissão concedida");
        return messaging.getToken();
      })
      .then(token => {
        console.log("Token de notificação:", token);
      })
      .catch(err => {
        console.error("Erro ao solicitar permissão de notificação:", err);
      });

    // Manipula mensagens recebidas em primeiro plano
    messaging.onMessage(payload => {
      alert(`Notificação recebida: ${payload.notification.title} - ${payload.notification.body}`);
    });
  }, []);

  // Função para simular clique de notificação
  const handleNotification = () => {
    alert("Botão clicado! Notificação gerada.");
  };

  return (
    <div className="app">
      <button className="notify-btn" onClick={handleNotification}>Clique para Notificação</button>
    </div>
  );
}

export default App;
