import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function DetalhesDesafio() {
  const { id } = useParams();
  const [detalhes, setDetalhes] = useState(null);

  useEffect(() => {
    const fetchDetalhes = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/desafios/${id}`);
        setDetalhes(response.data);
      } catch (error) {
        console.error('Erro ao buscar detalhes do desafio:', error);
      }
    };

    fetchDetalhes();
  }, [id]);

  if (!detalhes) return <p>Carregando...</p>;

  return (
    <div>
      <h2>Detalhes do Desafiosss</h2>
      <p>Descrição: {detalhes.descricao}</p>
      <p>Recompensa: {detalhes.recompensa}</p>
      <p>Data Limite: {detalhes.dataLimite ? new Date(detalhes.dataLimite).toLocaleDateString('pt-BR') : 'Sem data limite'}</p>
      <p>Autor: {detalhes.autorId}</p>
      <ul>
        <strong>Soluções:</strong>
        {detalhes.solucoes?.map((solucao, index) => (
          <li key={index}>{solucao}</li>
        ))}
      </ul>
    </div>
  );
}

export default DetalhesDesafio;
