import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Home() {
  const [desafios, setDesafios] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDesafios = async () => {
      try {
        const response = await axios.get('http://localhost:8080/desafios'); // Substitua pela URL do seu backend
        setDesafios(response.data);
      } catch (error) {
        console.error('Erro ao buscar desafios:', error);
      }
    };

    fetchDesafios();
  }, []);

  const handleExibirDetalhes = (id) => {
    navigate(`/desafios/${id}`);
  };

  return (
    <div>
      <h1>Lista de Desafios</h1>
      {desafios.map((desafio) => (
        <div key={desafio.id}>
          <h3>{desafio.titulo}</h3>
          <p>{desafio.descricao}</p>
          <button onClick={() => handleExibirDetalhes(desafio.id)}>Exibir detalhes</button>
        </div>
      ))}
    </div>
  );
}

export default Home;
