import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Home.css'; // Importa o arquivo de estilo

function Home() {
  const [desafios, setDesafios] = useState([]);
  const [usuario, setUsuario] = useState(null);
  const [detalhesDesafio, setDetalhesDesafio] = useState(null);
  const [novaSolucao, setNovaSolucao] = useState('');
  const [modalAberto, setModalAberto] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDesafios = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/desafios`);
        setDesafios(response.data);
      } catch (error) {
        console.error('Erro ao buscar desafios:', error);
      }
    };

    fetchDesafios();
  }, []);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        setUsuario(user);
      } else {
        setUsuario(null);
      }
    });
    return () => unsubscribe();
  }, []);

  const handleExibirDetalhes = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8080/desafios/${id}`);
      setDetalhesDesafio(response.data);
      setModalAberto(true); // Abre o modal
    } catch (error) {
      console.error('Erro ao buscar detalhes do desafio:', error);
    }
  };

  const handleAdicionarSolucao = async (id) => {
    try {
      await axios.post(`http://localhost:8080/desafios/${id}/solucoes`, {
        solucao: novaSolucao,
      });
      alert('Solução adicionada com sucesso');
      setNovaSolucao('');
      handleExibirDetalhes(id); // Atualiza os detalhes após adicionar
    } catch (error) {
      console.error('Erro ao adicionar solução:', error);
      alert('Erro ao adicionar solução');
    }
  };

  const closeModal = () => {
    setModalAberto(false); // Fecha o modal
  };

  return (
    <div className="container">
      <h1 className="title">Lista de Desafios</h1>
      <div className="cards-container">
        {desafios.map((desafio) => (
          <div key={desafio.id} className="card">
            <h2 className="card-title">{desafio.desafio}</h2>
            <p className="card-text"><strong>Recompensa:</strong> {desafio.recompensa}</p>
            <p className="card-text"><strong>Data Limite:</strong> {desafio.dataLimite}</p>
            <p className="card-text"><strong>Descrição:</strong> {desafio.descricao}</p>
            <button onClick={() => handleExibirDetalhes(desafio.id)} className="button-details">Exibir detalhes</button>
          </div>
        ))}
      </div>

      {modalAberto && detalhesDesafio && (
        <div className="modal">
          <div className="modal-content">
            <button className="close-modal" onClick={closeModal}>✖</button>
            <h2 className="modal-title">Detalhes do Desafio</h2>
            <p><strong>Descrição:</strong> {detalhesDesafio.descricao}</p>
            <p><strong>Recompensa:</strong> {detalhesDesafio.recompensa}</p>
            <p><strong>Data Limite:</strong> {detalhesDesafio.dataLimite ? new Date(detalhesDesafio.dataLimite).toLocaleDateString('pt-BR') : 'Sem data limite'}</p>
            <p><strong>Autor:</strong> {detalhesDesafio.autorId}</p>
            <input
              type="text"
              placeholder="Adicionar solução"
              value={novaSolucao}
              onChange={(e) => setNovaSolucao(e.target.value)}
              className="input-solucao"
            />
            <button onClick={() => handleAdicionarSolucao(detalhesDesafio.id)} className="button-save">Salvar</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Home;
